calc.function:
var s = row.PublishTime.getFullYear() + "." + (row.PublishTime.getMonth() + 1)
      + (row.PublishTime.getDate() >= 10 ? "." : ".0")
      + row.PublishTime.getDate() + " " + row.PublishTime.toLocaleTimeString() + "\n"
      + (row.PublisherFirstName || "") + " " + (row.PublisherLastName || "");
var ev = "";

switch(row.Event) {
  case 0:
    ev = "ACTIVATED";
    break;
  case 1:
    ev = "SUSPENDED";
    break;
  case 2:
    ev = "COMPLETED";
    break;
  case 3:
    ev = "APPROVED";
    break;
  case 4:
    ev = "CANCELED";
    break;
  case 5:
    ev = "FORWARDED";
    break;
  case 6:
    ev = "EXTENDED";
    break;
  case 7:
    ev = "RENEWED";
    break;
  case 8:
    ev = "COMMENTED";
    break;
  case 9:
    ev = "VISITED";
    break;
  case 10:
    ev = "UPDATED";
    break;
  case 10:
    ev = "DELETED";
    break;
}
s = s + "\n" + ev;
return s;

var s = "";

if (row.EventNote) {
  s = row.EventNote;
}
if (row.Comment) {
  if (s) {
    s = s + "\n";
  }
  s = s + row.Comment;
}
return s;

var s = "";

if (row.Duration) {
  s =  row.Duration.getTime() + " min." + "\n" + row.DurationType;
}
return s;
